//  + * - / numbers
function math(num3: number, num22: number, num32: number): number {
    if (num32 == 1) {
        return num3 + num22
    } else if (num32 == 0) {
        return num3 * num22
    } else if (num32 == 2) {
        return num3 - num22
    } else {
        return num3 / num22
    }
    
}
